mapper.update:
UPDATE room
floor_id = #{floorId}, room_type_id = #{roomTypeId}, room_no = #{roomNo}, room_status_id = #{roomStatusId}, remark = #{remark}, is_enabled = #{isEnabled},
WHERE id = #{id}

mapper.select:
SELECT r.id, r.hotel_id, r.floor_id, r.room_type_id, r.room_no, r.room_status_id, r.remark, r.is_enabled, r.created_at, r.updated_at, f.name AS floor_name, rt.name AS room_type_name, rs.name AS room_status_name FROM room r LEFT JOIN floor f ON r.floor_id = f.id LEFT JOIN room_type rt ON r.room_type_id = rt.id LEFT JOIN dict_room_status rs ON r.room_status_id = rs.id
AND r.hotel_id = #{hotelId} AND r.floor_id = #{floorId} AND r.room_type_id = #{roomTypeId} AND r.room_status_id = #{roomStatusId} AND r.room_no LIKE CONCAT('%', #{roomNo}, '%') AND r.is_enabled = #{isEnabled}
ORDER BY r.room_no ASC
