mapper.update:
UPDATE room_type
code = #{code}, name = #{name}, area = #{area}, bed_type_id = #{bedTypeId}, max_guests = #{maxGuests}, base_price = #{basePrice}, sort_order = #{sortOrder}, is_enabled = #{isEnabled},
WHERE id = #{id}

mapper.select:
SELECT rt.id, rt.hotel_id, rt.code, rt.name, rt.area, rt.bed_type_id, rt.max_guests, rt.base_price, rt.sort_order, rt.is_enabled, rt.created_at, rt.updated_at, bt.name AS bed_type_name FROM room_type rt LEFT JOIN dict_bed_type bt ON rt.bed_type_id = bt.id
AND rt.hotel_id = #{hotelId} AND rt.is_enabled = #{isEnabled}
ORDER BY rt.sort_order ASC

SELECT rt.id, rt.hotel_id, rt.code, rt.name, rt.area, rt.bed_type_id, rt.max_guests, rt.base_price, rt.sort_order, rt.is_enabled, rt.created_at, rt.updated_at, bt.name AS bed_type_name FROM room_type rt LEFT JOIN dict_bed_type bt ON rt.bed_type_id = bt.id
AND rt.hotel_id = #{hotelId} AND rt.name LIKE CONCAT('%', #{name}, '%') AND rt.bed_type_id = #{bedTypeId} AND rt.is_enabled = #{isEnabled}
ORDER BY rt.sort_order ASC
