mapper.update:
UPDATE room_type_price
price_type = #{priceType}, price = #{price}, effective_date = #{effectiveDate}, expire_date = #{expireDate}, is_default = #{isDefault},
WHERE id = #{id}

mapper.select:
SELECT 
FROM room_type_price
WHERE room_type_id = #{roomTypeId}
  AND price_type = #{priceType}
  AND (
    (effective_date IS NULL AND expire_date IS NULL)
    OR (#{date} BETWEEN effective_date AND expire_date)
  )
ORDER BY is_default ASC, effective_date DESC
LIMIT 1
